import React, { useState, useRef, useEffect } from 'react';

import EnemyGrid from './components/enemyGrid.js';
import PlayerGrid from './components/playerGrid.js';

import CardSelector from './components/cardSelector.js';
import PlayerSelector from './components/playerSelector.js';

import appLogo from './img/app-logo.png';

import axios from 'axios';
import { firebaseDatabase } from './utils/firebaseUtils.js';

const App = () => {

  const decksGridRef = React.createRef();
  const cardsGridRef = React.createRef();  

  const cardSelectorRef = useRef();  
  const playerGridRef = useRef();
  const enemyGridRef = useRef();

  const [isEnemyGridHidden, setIsEnemyGridHidden] = useState(false);
  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December']

  const cornerStyle = {
    width: 0, 
    height: 0, 
    borderTop: '100px solid rgb(42,221,119)', 
    borderLeft: '100px solid transparent',
    position: 'absolute',
    top: 0,
    right: 0
  }

  const getProperDate = (date) => {
    return String(date).length >= 2 ? date : '0' + String(date);
  }

  useEffect(() => {
    axios.get('http://ip-api.com/json').then(res => {      
      const currentTime = new Date();
      const year = String(currentTime.getFullYear());
      const month = months[currentTime.getMonth()];
      const day = getProperDate(currentTime.getDate());

      let count = 0;
      let countCountry = 0;
      let currentCity = res.data.city;

      firebaseDatabase.ref().child(year).child(month).child(day).child('visited_times').get().then((snapshot) => {
        if (snapshot.exists()) {
          count = snapshot.val() + 1;          
        } else {
          count = 1;
        }

        const newEntry = firebaseDatabase.ref().child(year).child(month).child(day).child('entries').push();

        newEntry.set(res.data.city + ', ' + res.data.country + ', ' + res.data.countryCode + ', ' + currentTime.toLocaleTimeString());

        firebaseDatabase.ref(year +'/' + month + '/' + day).update({
          visited_times: count        
        });

      });  

      firebaseDatabase.ref().child('countries').child(res.data.country).child('visited_times').get().then((snapshot) => {
        if (snapshot.exists()) {
          countCountry = parseInt(snapshot.val()) + 1;
        } else {
          countCountry = 1;
        }

        firebaseDatabase.ref().child('countries').child(res.data.country).child('cities').get().then((snapshot) => {
          if (snapshot.exists()) {
            if (snapshot.val().search(currentCity) === -1) {
              let aux = snapshot.val();
              aux += ', ' + currentCity;
              currentCity = aux;
            } else {
              currentCity = snapshot.val();
            }
          }

          firebaseDatabase.ref('countries').child(res.data.country).set({
            visited_times: countCountry,
            cities: currentCity,
            country_code: res.data.countryCode
          });

        });

      });

    }
    )
  }, []);

  const collapseEnemyGrid = () => {
    enemyGridRef.current.style.width = '0%';
    cardsGridRef.current.style.width = '58%';
    decksGridRef.current.style.width = '38%';
    setIsEnemyGridHidden(true);    
  }

  const showEnemyGrid = () => {
    enemyGridRef.current.style.width = '26%';
    cardsGridRef.current.style.width = '44%';
    decksGridRef.current.style.width = '26%';
    setIsEnemyGridHidden(false);    
  }

  const enemyGrid = () => {
    return (            
          <div>
          </div>
        );
  }

  return (
    <div className="main">
      <div className="row">
        <div className="section sides left">
          <PlayerGrid ref={playerGridRef} cardSelectorRef={cardSelectorRef} />
        </div>
        <div ref={cardsGridRef} className="section middle">
          <div style={{textAlign: 'center'}}>
            <img style={{width: '50%'}} src={appLogo} />
          </div>
          <CardSelector ref={cardSelectorRef} playerGridRef={playerGridRef} />
          <PlayerSelector cardSelectorRef={cardSelectorRef} playerGridRef={playerGridRef} enemyGridRef={enemyGridRef} />
          <p style={{textAlign: 'center', fontSize: '13px', color: '#555', margin: '4px 4px 90px 4px'}}>
            Programmed with ðŸ’– by <a href="https://twitter.com/carlospazuzu" target="_blank">@carlospazuzu</a>. <br /> 
            Compliments and Bug reports are appreciated.<br /> 
            Learn how to use here: <a href="https://bit.ly/3h8tuLd" target="_blank" style={{color: '#d6457e'}}>https://bit.ly/3h8tuLd</a> <br />
            It takes time and effort to build this tool for free community use <br /> If you find this tool useful, please consider<br />    
            <a href="https://www.buymeacoffee.com/carlospazuzu" target="_blank"><img src="https://cdn.buymeacoffee.com/buttons/v2/default-yellow.png" alt="Buy Me A Coffee" style={{height: '80px'}} /></a>
          </p>
        </div>
        <div className="section sides">
          <EnemyGrid ref={enemyGridRef} cardSelectorRef={cardSelectorRef} />
        </div>
        { /* !isEnemyGridHidden ? '' : <div style={cornerStyle} onClick={showEnemyGrid}></div> */ }  
      </div>
    </div>
  );

}


export default App;
