import React, { useState } from 'react';

import blueCrownImage from '../img/blueCrown.png';
import redCrownImage from '../img/redCrown.png';

const PlayerSelector = (props) => {

	const [currentMode, setCurrentMode] = useState('player');

	const changeModeToPlayer = () => {
		setCurrentMode('player');
		props.cardSelectorRef.current.changeCurrentMode('player');
		props.playerGridRef.current.changeCurrentMode('player');
		props.enemyGridRef.current.changeCurrentMode('player');
	}

	const changeModeToEnemy = () => {
		setCurrentMode('enemy');
		props.cardSelectorRef.current.changeCurrentMode('enemy');
		props.playerGridRef.current.changeCurrentMode('enemy');
		props.enemyGridRef.current.changeCurrentMode('enemy');
	}

	return(
		<div style={{marginTop: '15px'}} className="row">			
			<div style={{cursor: 'pointer', textAlign: 'center', backgroundColor: '#3cd', borderRadius: '10px', filter: currentMode === 'player' ? '' : 'grayscale(1.0)'}} className="six columns" onClick={changeModeToPlayer} >
				<img style={{width: '20%'}} src={blueCrownImage} />
			</div>
			<div style={{cursor: 'pointer', textAlign: 'center', backgroundColor: '#d3454e', borderRadius: '10px', filter: currentMode === 'enemy' ? '' : 'grayscale(1.0)'}} className="six columns" onClick={changeModeToEnemy}>
				<img style={{width: '20%'}} src={redCrownImage} />
			</div>
		</div>
	);
}


export default PlayerSelector;
