import React, { useState, useEffect } from 'react';

import cards from '../utils/card-data.js';
import bannedImg from '../img/banned.png';
import bannedStamp from '../img/banned-stamp.png';
import bannedStamp2 from '../img/banned-stamp2.png';
import xStamp from '../img/x-stamp.png';

const SelectableCard = (props) => {

	const [isPressingCtrl, setIsPressingCtrl] = useState(false);
	const [isBanned, setIsBanned] = useState(false);
	const [isBeingUsed, setisBeingUsed] = useState(false);

	const cardStyle = {
		width: '12%'
	}

	useEffect(() => {

		setIsBanned(props.isBanned);
		setisBeingUsed(props.isBeingUsed);
		/*
		document.addEventListener('keydown', (event) => {
			if (event.ctrlKey) {
				setIsPressingCtrl(true);				
			}
		});

		document.addEventListener('keyup', (event) => {			
			if (!event.ctrlKey) {
				setIsPressingCtrl(false);				
			}
		});		

		return () => {
			document.removeEventListener('keydown', (event) => {
			if (event.ctrlKey) {
				setIsPressingCtrl(true);				
			}
		});
			document.removeEventListener('keyup', (event) => {

			if (!event.ctrlKey) {
				setIsPressingCtrl(false);				
			}
		});
		}
		*/

	});

	const handleOnClick = () => {		
		if (isBeingUsed || props.transparent) return;
		// if (isPressingCtrl) props.funcUtils.toggleBannedCard(props.position);

		props.funcUtils.addCardToPlayerUsedList(props.position)

	}

	return (
		<span style={{cursor: !isBeingUsed ? 'pointer' : 'not-allowed', position: 'relative', opacity: 'transparent' in props && props.transparent === true ? '0.2' : '1.0'}} onClick={handleOnClick}>
			{ isBanned ? 
				<img src={bannedStamp2} style={{marginTop: '4px', padding: '1px', position: 'absolute', right: '0', left: '0', width: '100%', zIndex: '99', opacity: '0.75'}}/>
				: ''
			}					
			<img style={{width: '12%', filter: isBanned || isBeingUsed ? 'grayscale(1.0)' : 'none'}} src={cards['items'][props.position]['iconUrls']['medium']} />
		</span>
	);
}


export default SelectableCard;
