import React from 'react';


const CardFilterTab = React.forwardRef((props, ref) => {

	const cardFilterTabStyle = {
		display: 'inline-block',
		padding: '10px',	
		cursor: 'pointer',	
		fontSize: '12px'
	}

	const selfRef = React.createRef();	

	const typeStyle = {
		'C': {
			backgroundColor: '#d1e0f0',
			color: '#144244',
			border: '1px solid #d1e0f0',
		},
		'R': {
			backgroundColor: '#f7d560',
			color: '#bb7819',
			border: '1px solid #f7d560',
		},
		'E': {
			backgroundColor: '#c7b1cd',
			color: '#513058',
			border: '1px solid #c7b1cd',
		},
		'L': {
			backgroundColor: '#c7c4c7',
			color: '#3d3b3e',
			border: '1px solid #c7c4c7',
		},
		'CH': {
			backgroundColor: '#69719a',
			color: '#fae400',
			border: '1px solid #69719a',
		},
	}


	const resolveParentFunction = () => {
		if (props.cc === '') return;
		props.func(props.funcParameter, props.filterType);	
		ref.current.style.backgroundColor = typeStyle[props.cc].backgroundColor;
		ref.current.style.color = typeStyle[props.cc].color;
		ref.current.style.border = typeStyle[props.cc].border;
	}

	return (
		<div ref={ref} style={cardFilterTabStyle} className="cardFilterTab" onClick={resolveParentFunction} >
			{props.filterType}
		</div>
	);
});

export default CardFilterTab;
