import React, { useState, forwardRef, useImperativeHandle } from 'react';

import cards from '../utils/card-data.js';

import emptyCard from '../img/empty-card.png';

const PlayerDeckCard = forwardRef((props, ref) => {

	const [isEmpty, setIsEmpty] = useState(true);
	const [currentMode, setCurrentMode] = useState('player');

	useImperativeHandle(ref, () => ({
		changeCurrentMode: (newMode) => {
			setCurrentMode(newMode);
		}
	}));

	const getCardImg = (cardPosition) => {		
		return cards['items'][cardPosition]['iconUrls']['medium'];
	}

	const getRandomCard = () => {
		return Math.floor(Math.random() * 102);
	}

	const removeCard = () => {
		if (currentMode === 'enemy') return;

		props.funcUtils.removeCardFromDeck(props.position)
	}

	return(
		<span onClick={removeCard} style={{cursor: 'pointer'}}>			
			<img style={{width: '20%', display: 'inline-block'}} src={props.position !== -1 ? getCardImg(props.position) : emptyCard} />
		</span>
	);

});

export default PlayerDeckCard;
