import React, { useEffect } from 'react';


const TabSelector = (props) => {

	const selfRef = React.createRef();

	const tabStyle = {
		width: '16%',
		// height: '25px',
		borderTopLeftRadius: '8px',
		borderTopRightRadius: '8px',		
		backgroundColor: '#ccc',		
		display: 'inline-block',
		padding: '8px',		
		border: '1px solid #aaa',
		borderBottom: null,
		cursor: 'pointer',
		fontSize: '12px'
	}

	const typeStyle = {
		'C': {
			backgroundColor: '#d1e0f0',
			color: '#144244',
			border: '1px solid #d1e0f0',
		},
		'R': {
			backgroundColor: '#f7d560',
			color: '#bb7819',
			border: '1px solid #f7d560',
		},
		'E': {
			backgroundColor: '#c7b1cd',
			color: '#513058',
			border: '1px solid #c7b1cd',
		},
		'L': {
			backgroundColor: '#c7c4c7',
			color: '#3d3b3e',
			border: '1px solid #c7c4c7',
		},
		'CH': {
			backgroundColor: '#69719a',
			color: '#fae400',
			border: '1px solid #69719a',
		},
	}

	useEffect(() => {
		selfRef.current.style.backgroundColor = typeStyle[props.funcParameter].backgroundColor;
		selfRef.current.style.color = typeStyle[props.funcParameter].color;
		selfRef.current.style.border = typeStyle[props.funcParameter].border;
	}, []);

	const resolveParentFunction = () => {
		props.func(props.funcParameter);				
	}

	const backToOriginalColor = () => {
		selfRef.current.style.backgroundColor = '#ccc';
	}

	return (
		<div ref={selfRef} style={tabStyle} onClick={resolveParentFunction}>
			{props.title}
		</div>
	);
}


export default TabSelector;
