import React, { useState, forwardRef, useImperativeHandle } from 'react';

import TabSelector from './tabSelector.js';
import CardCollection from './cardCollection.js';
import CardFilterTab from './cardFilterTab.js';

const CardSelector = forwardRef((props, ref) => {

	const cardCollectionRef = React.createRef();

	const allFilterTabRef = React.createRef();
	const winConditionFilterTabRef = React.createRef();
	const spellFilterTabRef = React.createRef();
	const buildingFilterTabRef = React.createRef();
	const airTroopFilterTabRef = React.createRef();
	const splashFilterTabRef = React.createRef();

	const [currentFilterTab, setCurrentFilterTab] = useState(allFilterTabRef);
	const [bannedCardsList, setBannedCardsList] = useState([]);

	const [cardsSelectedByEnemy, setCardsSelectedByEnemy] = useState([]);
	const [cardsSelectedByPlayer, setCardsSelectedByPlayer] = useState([]);

	const [currentMode, setCurrentMode] = useState('player');

	useImperativeHandle(ref, () => ({
		changeEnemySelectionList: (mode, cardList) => {

			if (mode === 'add') {

				let auxArray = cardsSelectedByEnemy;				
				cardList.map(e => !auxArray.includes(e) ? auxArray.push(e) : null );
				setCardsSelectedByEnemy([...auxArray]);				

			} else if (mode === 'remove') {

				let auxArray = cardsSelectedByEnemy;
				for (let i = 0; i < auxArray.length; i ++) {
					let pos = auxArray.indexOf(cardList[i]);
					if (pos !== -1) {
						delete auxArray[pos];
					}
				}

				let auxArray2 = [];

				auxArray.map(e => e !== undefined ? auxArray2.push(e) : null);

				setCardsSelectedByEnemy(auxArray2);

			}
		},

		changeCurrentMode: (newMode) => {
			setCurrentMode(newMode);
		},

		removeCardFromPlayerUsedList: (cardNum) => {
			if (currentMode === 'enemy') return;

			let cardPosition = cardsSelectedByPlayer.indexOf(cardNum);

			if (cardPosition === -1) return;

			cardsSelectedByPlayer.splice(cardPosition, 1);

			setCardsSelectedByPlayer([...cardsSelectedByPlayer]);
		}
	}));


	const addCardToPlayerUsedList = (cardNum) => {

		if (currentMode === 'enemy') return;

		cardsSelectedByPlayer.push(cardNum);
		setCardsSelectedByPlayer([...cardsSelectedByPlayer]);		
		props.playerGridRef.current.addToAvailableDeck(cardNum);
	}


	const toggleBannedCard = (cardNum) => {

		let pos = bannedCardsList.indexOf(cardNum);

		if (pos !== -1) {

			delete bannedCardsList[pos];

			let auxArray = [];

			bannedCardsList.map(e => e !== undefined ? auxArray.push(e) : null);

			setBannedCardsList(auxArray);

		} else {

			if (bannedCardsList.length >= 2) return;

			bannedCardsList.push(cardNum);
			setBannedCardsList([...bannedCardsList]);
		}
	}

	const typeStyle = {
		'C': {
			backgroundColor: '#d1e0f0',
			color: '#144244',
			border: '1px solid #d1e0f0',
		},
		'R': {
			backgroundColor: '#f7d560',
			color: '#bb7819',
			border: '1px solid #f7d560',
		},
		'E': {
			backgroundColor: '#c7b1cd',
			color: '#513058',
			border: '1px solid #c7b1cd',
		},
		'L': {
			backgroundColor: '#c7c4c7',
			color: '#3d3b3e',
			border: '1px solid #c7c4c7',
		},
		'CH': {
			backgroundColor: '#69719a',
			color: '#fae400',
			border: '1px solid #69719a',
		},
		'None': {
			backgroundColor: '#aaa',
			border: '1px solid #aaa',
			color: '#111'
		}
	}

	const [currentCollection, setCurrentCollection] = useState('');
	const [currentFilter, setCurrentFilter] = useState('');

	const changeCollection = (collection) => {	

		setCurrentCollection(collection);		
		cardCollectionRef.current.style.backgroundColor = typeStyle[collection].backgroundColor;	

		switch(currentFilterTab) {
			case 'All':
				allFilterTabRef.current.style.backgroundColor = typeStyle[collection].backgroundColor;
				allFilterTabRef.current.style.color = typeStyle[collection].color;
				allFilterTabRef.current.style.border = typeStyle[collection].border;
				break;
			case 'Win Condition':
				winConditionFilterTabRef.current.style.backgroundColor = typeStyle[collection].backgroundColor;
				winConditionFilterTabRef.current.style.color = typeStyle[collection].color;
				winConditionFilterTabRef.current.style.border = typeStyle[collection].border;
				break;
			case 'Spell':
				spellFilterTabRef.current.style.backgroundColor = typeStyle[collection].backgroundColor;
				spellFilterTabRef.current.style.color = typeStyle[collection].color;
				spellFilterTabRef.current.style.border = typeStyle[collection].border;
				break;
			case 'Building':
				buildingFilterTabRef.current.style.backgroundColor = typeStyle[collection].backgroundColor;
				buildingFilterTabRef.current.style.color = typeStyle[collection].color;
				buildingFilterTabRef.current.style.border = typeStyle[collection].border;
				break;
			case 'Air Troop':
				airTroopFilterTabRef.current.style.backgroundColor = typeStyle[collection].backgroundColor;
				airTroopFilterTabRef.current.style.color = typeStyle[collection].color;
				airTroopFilterTabRef.current.style.border = typeStyle[collection].border;
				break;
			case 'Splash':
				splashFilterTabRef.current.style.backgroundColor = typeStyle[collection].backgroundColor;
				splashFilterTabRef.current.style.color = typeStyle[collection].color;
				splashFilterTabRef.current.style.border = typeStyle[collection].border;
				break;
		}

	}

	const changeFilter = (filter, callingFilter) => {
		setCurrentFilter(filter);
		setCurrentFilterTab(callingFilter);

		allFilterTabRef.current.style.backgroundColor = typeStyle['None'].backgroundColor;
		allFilterTabRef.current.style.color = typeStyle['None'].color;
		allFilterTabRef.current.style.border = typeStyle['None'].border;

		winConditionFilterTabRef.current.style.backgroundColor = typeStyle['None'].backgroundColor;
		winConditionFilterTabRef.current.style.color = typeStyle['None'].color;
		winConditionFilterTabRef.current.style.border = typeStyle['None'].border;

		spellFilterTabRef.current.style.backgroundColor = typeStyle['None'].backgroundColor;
		spellFilterTabRef.current.style.color = typeStyle['None'].color;
		spellFilterTabRef.current.style.border = typeStyle['None'].border;

		buildingFilterTabRef.current.style.backgroundColor = typeStyle['None'].backgroundColor;
		buildingFilterTabRef.current.style.color = typeStyle['None'].color;
		buildingFilterTabRef.current.style.border = typeStyle['None'].border;

		airTroopFilterTabRef.current.style.backgroundColor = typeStyle['None'].backgroundColor;
		airTroopFilterTabRef.current.style.color = typeStyle['None'].color;
		airTroopFilterTabRef.current.style.border = typeStyle['None'].border;

		splashFilterTabRef.current.style.backgroundColor = typeStyle['None'].backgroundColor;
		splashFilterTabRef.current.style.color = typeStyle['None'].color;
		splashFilterTabRef.current.style.border = typeStyle['None'].border;

	}

	return (
		<div className="row">
			<div className="row">
				<TabSelector title={'Common'} func={changeCollection} funcParameter={'C'} />
				<TabSelector title={'Rare'} func={changeCollection} funcParameter={'R'} />
				<TabSelector title={'Epic'} func={changeCollection} funcParameter={'E'}  />
				<TabSelector title={'Legendary'} func={changeCollection} funcParameter={'L'} />
				<TabSelector title={'Hero'} func={changeCollection} funcParameter={'CH'} />
			</div>
			<div ref={cardCollectionRef} className="row cardPoolSection">
				<CardCollection bannedCardsList={bannedCardsList} usedCardsList={currentMode === 'player' ? cardsSelectedByPlayer : cardsSelectedByEnemy} funcUtils={{'toggleBannedCard': toggleBannedCard, 'addCardToPlayerUsedList': addCardToPlayerUsedList}} visible={ currentCollection === 'C' ? true : false } type={'C'} filter={currentFilter} />
				<CardCollection bannedCardsList={bannedCardsList} usedCardsList={currentMode === 'player' ? cardsSelectedByPlayer : cardsSelectedByEnemy} funcUtils={{'toggleBannedCard': toggleBannedCard, 'addCardToPlayerUsedList': addCardToPlayerUsedList}} visible={ currentCollection === 'R' ? true : false } type={'R'} filter={currentFilter} />
				<CardCollection bannedCardsList={bannedCardsList} usedCardsList={currentMode === 'player' ? cardsSelectedByPlayer : cardsSelectedByEnemy} funcUtils={{'toggleBannedCard': toggleBannedCard, 'addCardToPlayerUsedList': addCardToPlayerUsedList}} visible={ currentCollection === 'E' ? true : false } type={'E'} filter={currentFilter} />
				<CardCollection bannedCardsList={bannedCardsList} usedCardsList={currentMode === 'player' ? cardsSelectedByPlayer : cardsSelectedByEnemy} funcUtils={{'toggleBannedCard': toggleBannedCard, 'addCardToPlayerUsedList': addCardToPlayerUsedList}} visible={ currentCollection === 'L' ? true : false } type={'L'} filter={currentFilter} />
				<CardCollection bannedCardsList={bannedCardsList} usedCardsList={currentMode === 'player' ? cardsSelectedByPlayer : cardsSelectedByEnemy} funcUtils={{'toggleBannedCard': toggleBannedCard, 'addCardToPlayerUsedList': addCardToPlayerUsedList}} visible={ currentCollection === 'CH' ? true : false } type={'CH'} filter={currentFilter} />
				{ 
					currentCollection === '' ? 
					<div style={{color: '#555', width: '100%', height: '175px', textAlign: 'center', marginTop: '125px'}}>
						To start, Please select a card rarity. <br />						
					</div> 
					: '' 
				}				
			</div>
			<div style={{textAlign: 'center'}}>
				<CardFilterTab ref={allFilterTabRef} filterType={'All'} func={changeFilter} funcParameter={''} cc={currentCollection} />
				<CardFilterTab ref={winConditionFilterTabRef} filterType={'Win Condition'} func={changeFilter} funcParameter={'W'} cc={currentCollection} />
				<CardFilterTab ref={spellFilterTabRef} filterType={'Spell'} func={changeFilter} funcParameter={'S'} cc={currentCollection} />
				<CardFilterTab ref={buildingFilterTabRef} filterType={'Building'} func={changeFilter} funcParameter={'B'} cc={currentCollection} />
				<CardFilterTab ref={airTroopFilterTabRef} filterType={'Air Troop'} func={changeFilter} funcParameter={'A'} cc={currentCollection} />
				<CardFilterTab ref={splashFilterTabRef} filterType={'Splash'} func={changeFilter} funcParameter={'SP'} cc={currentCollection} />
			</div>
		</div>
	);
});

export default CardSelector;
